import { useState } from "react";
import { Button, Table } from "react-bootstrap";
import { Pencil } from "react-bootstrap-icons";
import { Exercise, ExerciseTrackerExerciseTableProps } from "../../Interfaces/Exercise";
import ExerciseTrackerExerciseEditModal from "./ExerciseTrackerExerciseEditModal";

function ExerciseTrackerExerciseTable({data, day, reload}: ExerciseTrackerExerciseTableProps) {
  const [exercise, setExercise] = useState<Exercise>();
  const [showEditModal, setShowEditModal] = useState(false);

  const handleEdit = (exercise: Exercise) => {
    setExercise(exercise);
    setShowEditModal(true);
  };

  return (
    <>
    <Table className="mt-4 mb-0" responsive={true}>
      <thead>
        <tr>
          <th>Name</th>
          <th>Weight</th>
          <th>Reps</th>
          <th>Sets</th>
          <th>Edit</th>
        </tr>
      </thead>
      <tbody>
        {data?.filter(e => e.dayId === day.id).sort((a, b) => a.name.localeCompare(b.name)).map((e =>
          <tr>
            <td>{e.name}</td>
            <td>{e.weight}</td>
            <td>{e.reps}</td>
            <td>{e.sets}</td>
            <td><Button className="btn-edit d-flex" onClick={() => handleEdit(e)}><Pencil /></Button></td>
          </tr>
        ))}
      </tbody>
    </Table>

    {showEditModal ? <ExerciseTrackerExerciseEditModal day={day} exercise={exercise!} show={showEditModal} setShow={setShowEditModal} reload={reload} /> : <span>foo</span> }
    </>
  );
}

export default ExerciseTrackerExerciseTable;
