import { useState } from "react";
import { Col, Container, Row } from "react-bootstrap";
import ExerciseTracker from "../Components/ExerciseTracker/ExerciseTracker";
import Sidebar from "../Components/Sidebar";
import WeightTracker from "../Components/WeightTracker/WeightTracker";

function Dashboard() {
  const [showWeightModal, setShowWeightModal] = useState(false);
  const [showCreateDayModal, setShowCreateDayModal] = useState(false);

  return (
    <Container fluid={true}>
      <Row>
        <Col md="auto" className="mt-4">
          <Sidebar
            setShowWeightModal={setShowWeightModal}
            setShowCreateDayModal={showCreateDayModal}
          />
        </Col>

        <Col className="mb-4">
          <WeightTracker show={showWeightModal} setShow={setShowWeightModal} />

          <ExerciseTracker showCreateDayModal={showCreateDayModal} setShowCreateDayModal={setShowCreateDayModal} />
        </Col>
      </Row>
    </Container>
  );
}

export default Dashboard;
