import { useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";
import { createRecord, latestRecord, recordExists, updateRecord } from "../Helpers/WeightHelpers";
import { WeightTrackerModalProps } from "../Interfaces/Weight";

function WeightTrackerModal({data, show, setShow, reload}: WeightTrackerModalProps) {
  const latest = latestRecord(data!)!;

  const [currentWeight, setCurrentWeight] = useState(latest.currentWeight);
  const [goalWeight, setGoalWeight] = useState(latest.goalWeight);

  const handleClose = () => setShow(false);

  const handleSubmit = async (e: React.MouseEvent<HTMLButtonElement, MouseEvent>) => {
    e.preventDefault();

    const currentDate = new Date();

    const response = recordExists(data!)
      ? await updateRecord(latest.id, latest.currentDate, currentWeight, goalWeight)
      : await createRecord(currentDate, currentWeight, goalWeight);

    if (response.ok) {
      handleClose();
    }
    else {
      alert("Fuck");
    }
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Body>
        <Modal.Title className="mb-3">Weight Tracker</Modal.Title>

        <Form>
          <Form.Group>
            <Form.Label>Current Weight</Form.Label>
            <Form.Control
              className="mb-3"
              onChange={(e) => setCurrentWeight(Number(e.target.value))}
              placeholder="What is your current weight?"
            />
          </Form.Group>

          <Form.Group>
            <Form.Label>Goal Weight</Form.Label>
            <Form.Control
              className="mb-3"
              defaultValue={latest.goalWeight}
              onChange={(e) => setGoalWeight(Number(e.target.value))}
              placeholder="What is your goal weight?"
            />
          </Form.Group>

          <Form.Group className="d-flex justify-content-end">
            <Button className="foo me-3" onClick={handleClose}>Close</Button>
            <Button className="bar" onClick={handleSubmit} type="submit">Submit</Button>
          </Form.Group>
        </Form>
      </Modal.Body>
    </Modal>
  );
}

export default WeightTrackerModal;
