import { useState } from "react";
import { Col, Container, Row } from "react-bootstrap";
import ExerciseTracker from "./ExerciseTracker/ExerciseTracker";
import Sidebar from "./Sidebar";
import WeightTracker from "./WeightTracker/WeightTracker";

interface DashboardProps {
  isAuthenticated: boolean
}

function Dashboard({isAuthenticated}: DashboardProps) {
  const [showWeightModal, setShowWeightModal] = useState(false);
  const [showDayModal, setShowDayModal] = useState(false);

  return (
    <Container fluid={true}>
      <Row>
        <Col md="auto" className="mt-4">
          <Sidebar
            setShowWeightModal={setShowWeightModal}
            setShowDayModal={setShowDayModal}
          />
        </Col>

        <Col className="mb-4">
          <WeightTracker show={showWeightModal} setShow={setShowWeightModal} />

          <ExerciseTracker show={showDayModal} setShow={setShowDayModal} />
        </Col>
      </Row>
    </Container>
  );
}

export default Dashboard;
