import { Button, Form, Modal } from "react-bootstrap";
import { WeightTrackerModalProps } from "../Interfaces/Weight";

function WeightTrackerModal({data, show}: WeightTrackerModalProps) {
  const latest = data!.at(-1)!; // Get the last element from the array

  return (
    <Modal show={showModal} onHide={handleCloseModal}>
      <Modal.Body>
        <Modal.Title>Weight Tracker</Modal.Title>

        <Form>
          <Form.Group>
            <Form.Label>Current Weight</Form.Label>
            <Form.Control
              placeholder="What is your current weight?"
              onChange={(e) => setCurrentWeight(Number(e.target.value))}
            />
          </Form.Group>

          <Form.Group>
            <Form.Label>Goal Weight</Form.Label>
            <Form.Control
              placeholder="What is your goal weight?"
              defaultValue={latest.goalWeight} onChange={(e) => setGoalWeight(Number(e.target.value))}
            />
          </Form.Group>

          <Button onClick={handleCloseModal}>Close</Button>
          <Button type="submit" onClick={handleSubmit}>Submit</Button>
        </Form>
      </Modal.Body>
    </Modal>
  );
}

export default WeightTrackerModal;
