import { useAuth0 } from "@auth0/auth0-react";
import { useCallback, useEffect, useState } from "react";
import { Button, Card, Col, OverlayTrigger, Row, Tooltip } from "react-bootstrap";
import { PlusCircle, Trash3Fill } from "react-bootstrap-icons";
import { daysSortedByName } from "../../Helpers/ExerciseTrackerHelpers";
import { Day } from "../../Interfaces/Day";
import { ExerciseTrackerProps } from "../../Interfaces/Exercise";
import { get } from "../../Repository";
import Loading from "../Loading";
import ExerciseTrackerDayCreateModal from "./ExerciseTrackerDayCreateModal";

function ExerciseTracker({show, setShow}: ExerciseTrackerProps) {
  const [data, setData] = useState<Day[]>();
  const [loading, setLoading] = useState(true);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false);

  const handleDelete = () => setShowDeleteModal(true);
  const handleCreate = () => setShowCreateModal(true);

  const { getAccessTokenSilently } = useAuth0();

  const fetchData = useCallback(async () => {
    const token = await getAccessTokenSilently();
    const data = await get("http://167.179.146.115:5000/api/day", token);

    setData(data);
    setLoading(false);
  }, [getAccessTokenSilently]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  return (
    <>
      {loading ? <Loading /> : <Row>
        {daysSortedByName(data!).map(day => (
          <Col md={4} className="mt-4">
            <Card>
              <Card.Body>
                <Card.Title className="mb-3">
                  {day.name}

                  <OverlayTrigger
                    key="top"
                    placement="top"
                    overlay={
                      <Tooltip className="tooltip" id="top">Delete Day</Tooltip>
                    }
                  >
                    <Button className="btn-trash float-end ms-3" onClick={handleDelete}><Trash3Fill /></Button>
                  </OverlayTrigger>

                  <OverlayTrigger
                    key="top"
                    placement="top"
                    overlay={
                      <Tooltip id="top">Add Exercise</Tooltip>
                    }
                  >
                    <Button className="btn-add float-end ms-3" onClick={handleCreate}><PlusCircle /></Button>
                  </OverlayTrigger>
                </Card.Title>
              </Card.Body>
            </Card>
          </Col>
        ))}

        <ExerciseTrackerDayCreateModal show={show} setShow={setShow} reload={fetchData} />
      </Row>}
    </>
  );
}

export default ExerciseTracker;
