import { Day } from "./Interfaces/Day";
import { Exercise } from "./Interfaces/Exercise";
import { Weight } from "./Interfaces/Weight";

export async function get(url: string, token: string) {
  const response = await fetch(url, {
    method: "GET",
    headers: {
      "Authorization": `Bearer ${token}`
    }
  })

  return response.ok ? await response.json() : alert("Fuck");
}

export async function post(url: string, token: string, body: Day | Exercise | Weight) {
  try {
  const response = await fetch(url, {
    method: "POST",
    headers: {
      "Authorization": `Bearer ${token}`
    },
    body: JSON.stringify(body),
  })

  return response.ok ? await response.json() : alert(response.body);
  }
  catch (e) {
    alert(e);
  }
}
