import { useAuth0 } from "@auth0/auth0-react";
import { useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";
import { createRecord, dateFormatter, latest, latestRecord, recordExists, updateRecord } from "../../Helpers/WeightTrackerHelpers";
import { CreateWeightModalProps } from "../../Interfaces/Weight";

function CreateWeightModal({weights, show, setShow, reload}: CreateWeightModalProps) {
  const [currentWeight, setCurrentWeight] = useState(latest(weights)?.currentWeight);
  const [currentDate, setCurrentDate] = useState(latest(weights)?.currentDate);
  const [goalWeight, setGoalWeight] = useState(latest(weights)?.goalWeight);

  const { getAccessTokenSilently } = useAuth0();

  const handleClose = () => setShow(false);

  const handleSubmit = async (e: React.MouseEvent<HTMLButtonElement, MouseEvent>) => {
    e.preventDefault();

    const token = await getAccessTokenSilently();

    dateFormatter(latest.currentDate) === dateFormatter(currentDate)
      ? await updateRecord(latest.id, latest.currentDate, currentWeight, goalWeight)
      : await createRecord(currentDate, currentWeight, goalWeight);

    reload();
    handleClose();
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Body>
        <Modal.Title className="mb-3">Record Weight</Modal.Title>

        <Form>
          <Form.Group>
            <Form.Label>Current Weight</Form.Label>
            <Form.Control
              className="mb-3"
              onChange={(e) => setCurrentWeight(Number(e.target.value))}
              placeholder="What is your current weight?"
            />
          </Form.Group>

          <Form.Group>
            <Form.Label>Goal Weight</Form.Label>
            <Form.Control
              className="mb-3"
              defaultValue={latest.goalWeight}
              onChange={(e) => setGoalWeight(Number(e.target.value))}
              placeholder="What is your goal weight?"
            />
          </Form.Group>

          <Form.Group className="d-flex justify-content-end">
            <Button className="btn-cancel me-3" onClick={handleClose}>Close</Button>
            <Button className="btn-submit" onClick={handleSubmit} type="submit">Submit</Button>
          </Form.Group>
        </Form>
      </Modal.Body>
    </Modal>
  );
}

export default CreateWeightModal;
