import {
  Area,
  AreaChart,
  Legend,
  ResponsiveContainer,
  Tooltip,
  XAxis,
  YAxis
} from "recharts";
import { WeightTrackerAreaChartProps } from "../Interfaces/Weight";

function WeightTrackerAreaChart({data}: WeightTrackerAreaChartProps) {
  const dateFormatter = (d: Date) => {
    const date = new Date(d);
    const month = date.toLocaleDateString("en-AU", { month: "short" }); // Jan
    const day = date.toLocaleDateString("en-AU", { day: "2-digit" }); // 01

    return `${month} ${day}`; // Jan 01
  };
  const dataFormatter = () => {
    return data!.map((w) => ({
      "Date": dateFormatter(w.currentDate),
      "Weight": w.currentWeight,
      "Goal Weight": w.goalWeight
    }));
  };
  const minValue = Math.min(...data!.map((w) => w.goalWeight)) - 2;
  const maxValue = Math.max(...data!.map((w) => w.currentWeight)) + 2;

  return (
    <ResponsiveContainer width="100%" height={350}>
      <AreaChart data={data} margin={{top: 0, right: 24, bottom: 0, left: 24}}>
        <defs>
          <linearGradient id="weight" x1="0" y1="0" x2="0" y2="1">
            <stop offset="5%" stopColor="#8be9fd" stopOpacity={0.5} />
            <stop offset="95%" stopColor="#8be9fd" stopOpacity={0} />
          </linearGradient>

          <linearGradient id="goal-weight" x1="0" y1="0" x2="0" y2="1">
            <stop offset="5%" stopColor="#bd93f9" stopOpacity={0.5} />
            <stop offset="95%" stopColor="#bd93f9" stopOpacity={0} />
          </linearGradient>
        </defs>

        <Legend align="right" verticalAlign="top" iconType="circle" iconSize={8} />

        <Tooltip
          wrapperStyle={{outline: "none"}}
          contentStyle={{color: "#f8f8f2", borderColor: "#44475a", backgroundColor: "#282a36"}}
          cursor={false}
        />

        <XAxis dataKey="Date" axisLine={false} tick={{fill: "#44475a"}} tickLine={false} />
        <YAxis domain={[minValue, maxValue]} hide={true} />

        <Area dataKey="Weight" stroke="#8be9fd" strokeWidth={2} fill="url(#weight)" fillOpacity={0.8} />
        <Area dataKey="Goal Weight" stroke="#bd93f9" strokeWidth={2} fill="url(#goal-weight)" fillOpacity={0.2} />
      </AreaChart>
    </ResponsiveContainer>
  );
}

export default WeightTrackerAreaChart;
