import { useState } from "react";
import { Card, Col, Row } from "react-bootstrap";
import { Weight, WeightTrackerProps } from "../../Interfaces/Weight";
import Loading from "../Loading";
import WeightTrackerAreaChart from "./WeightTrackerAreaChart";
import WeightTrackerDifference from "./WeightTrackerDifference";
import WeightTrackerModal from "./WeightTrackerModal";

function WeightTracker({show, setShow, reload}: WeightTrackerProps) {
  const [data, setData] = useState<Weight[]>();
  const [loading, setLoading] = useState(true);

  const fetchData = async () => {
    const response = await fetch("http://localhost:5000/api/weight");

    if (response.ok) {
      const data = await response.json();

      setData(data);
      setLoading(false);
    }
    else {
      alert("Fuck");
    }
  };

  return (
    <Row>
      <Col md={8} className="mt-4">
        <Card>
          {loading ? <Loading /> : <WeightTrackerAreaChart data={data} />}
        </Card>
      </Col>

      <Col md={4} className="mt-4">
        <Card>
          {loading ? <Loading /> : <WeightTrackerDifference data={data} />}
        </Card>
      </Col>

      <WeightTrackerModal data={data} show={show} setShow={setShow} reload={reload} />
    </Row>
  );
}

export default WeightTracker;
