import { useAuth0 } from "@auth0/auth0-react";
import { useState } from "react";
import { Button, Col, Container, Row } from "react-bootstrap";
import ExerciseTracker from "./Components/ExerciseTracker/ExerciseTracker";
import Sidebar from "./Components/Sidebar";
import WeightTracker from "./Components/WeightTracker/WeightTracker";

function App() {
  const [showWeightModal, setShowWeightModal] = useState(false);
  const [showDayModal, setShowDayModal] = useState(false);
  const { loginWithRedirect, isAuthenticated } = useAuth0();

  return (
    <Container fluid={true}>
      {isAuthenticated ?
        <Row>
          <Col md="auto" className="mt-4">
            <Sidebar
              setShowWeightModal={setShowWeightModal}
              setShowDayModal={setShowDayModal}
            />
          </Col>

          <Col className="mb-4">
            <WeightTracker
              show={showWeightModal}
              setShow={setShowWeightModal}
            />

            <ExerciseTracker
              show={showDayModal}
              setShow={setShowDayModal}
            />
          </Col>
        </Row> : 
        <Button onClick={() => loginWithRedirect()} />
      }
    </Container>
  );
}

export default App;
