import { useEffect, useState } from "react";
import { Card, Col, Row } from "react-bootstrap";
import { latestRecord } from "../../Helpers/WeightHelpers";
import { Weight, WeightTrackerProps } from "../../Interfaces/Weight";
import Loading from "../Loading";
import WeightTrackerAreaChart from "./WeightTrackerAreaChart";
import WeightTrackerDifference from "./WeightTrackerDifference";
import WeightTrackerModal from "./WeightTrackerModal";

function WeightTracker({show, setShow, reload}: WeightTrackerProps) {
  const [data, setData] = useState<Weight[]>();
  const [loading, setLoading] = useState(true);

  const latest = latestRecord(data!)!;
  const diff = (latest.currentWeight - latest.goalWeight).toFixed(2);

  const fetchData = async () => {
    const response = await fetch("http://localhost:5000/api/weight");

    if (response.ok) {
      const data = await response.json();

      setData(data);
      setLoading(false);
    }
    else {
      alert("Fuck");
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <>
      {loading ? <Loading /> : <Row>
        <Col md={8} className="mt-4">
          <Card>
            <Card.Body>
              <Card.Title>Weight Tracker</Card.Title>
              <WeightTrackerAreaChart data={data} />
            </Card.Body>
          </Card>
        </Col>

        <Col md={4} className="mt-4">
          <Card>
            <Card.Body>
              <Card.Title>Weight Difference</Card.Title>
              <span className="weight-difference">{diff} kg</span>
            </Card.Body>
          </Card>
        </Col>

        <WeightTrackerModal data={data} show={show} setShow={setShow} reload={reload} />
      </Row>}
    </>
  );
}

export default WeightTracker;
