import { Weight } from "../Interfaces/Weight";

export function dateFormatter(d: Date) {
  const date = new Date(d);
  const month = date.toLocaleDateString("en-AU", { month: "short" }); // Jan
  const day = date.toLocaleDateString("en-AU", { day: "2-digit" }); // 01

  return `${month} ${day}`; // Jan 01
}

export function dataFormatter(d: Weight[]) {
  return d.map((w) => ({
    "Date": dateFormatter(w.currentDate),
    "Weight": w.currentWeight,
    "Goal Weight": w.goalWeight
  }));
}

export function latestRecord(d: Weight[]) {
  return d.at(-1)!; // Get the last element from the array
}

export function recordExists(d: Weight[]) {
  const currentDate = new Date();
  const latestRecordCurrentDate = latestRecord(d).currentDate;
  return dateFormatter(currentDate) === dateFormatter(latestRecordCurrentDate);
}

export function updateRecord() {}

export function createRecord() {}
