import { Button, Form, Modal } from "react-bootstrap";
import { ExerciseTrackerDayDeleteModalProps } from "../../Interfaces/Day";

function ExerciseTrackerDayDeleteModal({day, show, setShow, reload}: ExerciseTrackerDayDeleteModalProps) {
  const handleClose = () => setShow(false);

  const handleSubmit = async (e: React.MouseEvent<HTMLButtonElement, MouseEvent>) => {
    e.preventDefault();

    const response = await fetch(`http://167.179.146.115:5000/api/day/${day.id}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
      }
    });

    if (response.ok) {
      handleClose();
      reload();
    }
    else {
      alert("Fuck");
    }
  };

  return (
    <Modal show={show} onHide={handleClose} centered={true}>
      <Modal.Title className="mb-3">Delete Exercise Day</Modal.Title>

      <Modal.Body>
        <p>Are you sure you want to delete {day.name}?</p>

        <Form>
          <Form.Group className="d-flex justify-content-end">
            <Button className="btn-cancel me-3" onClick={handleClose}>Close</Button>
            <Button className="btn-submit" onClick={handleSubmit} type="submit">Delete</Button>
          </Form.Group>
        </Form>
      </Modal.Body>
    </Modal>
  );
}

export default ExerciseTrackerDayDeleteModal;
