import { useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";
import { Exercise, ExerciseTrackerDayCreateModalProps } from "../../Interfaces/Exercise";

function ExerciseTrackerExerciseCreateModal({day, show, setShow, reload}: ExerciseTrackerDayCreateModalProps) {
  const [name, setName] = useState("");
  const [weight, setWeight] = useState(0);
  const [reps, setReps] = useState(0);
  const [sets, setSets] = useState(0);

  const handleClose = () => setShow(false);

  const handleSubmit = async (e: React.MouseEvent<HTMLButtonElement, MouseEvent>) => {
    e.preventDefault();

    const exercise: Exercise = {
      dayId: day.id!,
      name: name,
      weight: weight,
      reps: reps,
      sets: sets
    };

    alert(exercise.dayId);

    reload();
  };

  return (
    <Modal show={show} onHide={handleClose} centered={true}>
      <Modal.Body>
        <Modal.Title className="mb-3">Exercise Tracker</Modal.Title>

        <Form>
          <Form.Group>
            <Form.Label>Name</Form.Label>
            <Form.Control
              className="mb-3"
              onChange={(e) => setName(e.target.value)}
              placeholder="What is the name of the exercise?"
            />
          </Form.Group>

          <Form.Group>
            <Form.Label>Weight</Form.Label>
            <Form.Control
              className="mb-3"
              onChange={(e) => setWeight(Number(e.target.value))}
              placeholder="What is the weight used for the exercise?"
            />
          </Form.Group>

          <Form.Group className="d-flex justify-content-end">
            <Button className="btn-cancel me-3" onClick={handleClose}>Close</Button>
            <Button className="btn-submit" onClick={handleSubmit} type="submit">Submit</Button>
          </Form.Group>
        </Form>
      </Modal.Body>
    </Modal>
  );
}

export default ExerciseTrackerExerciseCreateModal;
