import { useEffect, useState } from "react";
import { Card, Col, Container, Row } from "react-bootstrap";
import { Weight } from "./Interfaces/Weight";
import Loading from "./Components/Loading";
import Sidebar from "./Components/Sidebar";
import WeightDifference from "./Components/WeightDifference";
import WeightTrackerAreaChart from "./Components/WeightTrackerAreaChart";

function App() {
  const [data, setData] = useState<Weight[]>();
  const [loading, setLoading] = useState(true);
  const [show, setShow] = useState(false);

  const fetchData = async () => {
    const response = await fetch("http://localhost:5000/api/weight");

    if (response.ok) {
      const data = await response.json();

      setData(data);
      setLoading(false);
    }
    else {
      // Fuck
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <Container fluid={true}>
      <Row>
        <Col md="auto" className="mt-4">
          <Sidebar setShow={setShow} />
        </Col>

        <Col>
          <Row>
            <Col md={8} className="mt-4">
              <Card>
                {loading ? <Loading /> : <WeightTrackerAreaChart data={data} />}
              </Card>
            </Col>

            <Col md={4} className="mt-4">
              <WeightDifference />
            </Col>
          </Row>
        </Col>
      </Row>
    </Container>
  );
}

export default App;
