import { useEffect, useState } from "react";
import { Card, Col, Container, Row } from "react-bootstrap";
import Loading from "./Components/Loading";
import Sidebar from "./Components/Sidebar";
import { Day } from "./Interfaces/Day";
import Foo from "./Components/Day";
import DayModal from "./Components/DayModal";
import WeightTracker from "./Components/WeightTracker/WeightTracker";
import ExerciseTracker from "./Components/ExerciseTracker/ExerciseTracker";

function App() {
  const [data, setData] = useState<Day[]>();
  const [loading, setLoading] = useState(true);
  const [showWeightModal, setShowWeightModal] = useState(false);
  const [showDayModal, setShowDayModal] = useState(false);

  const fetchData = async () => {
    const response = await fetch("http://localhost:5000/api/day")

    if (response.ok) {
      const data = await response.json();

      setData(data);
      setLoading(false);
    }
    else {
      alert("Fuck");
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <Container fluid={true}>
      <Row>
        <Col md="auto" className="mt-4">
          <Sidebar
            setShowWeightModal={setShowWeightModal}
            setShowDayModal={setShowDayModal}
          />
        </Col>

        <Col>
          <WeightTracker
            show={showWeightModal}
            setShow={setShowWeightModal}
          />

          <ExerciseTracker />
        </Col>
      </Row>
    </Container>
  );
}

export default App;
