import { useAuth0 } from "@auth0/auth0-react";
import { useEffect, useState } from "react";
import { Button, Card, Col, Row } from "react-bootstrap";
import { daysSortedByName } from "../../Helpers/ExerciseTrackerHelpers";
import { Day } from "../../Interfaces/Day";
import { ExerciseTrackerProps } from "../../Interfaces/Exercise";
import { get } from "../../Repository";
import Loading from "../Loading";
import ExerciseTrackerDay from "./ExerciseTrackerDay";
import ExerciseTrackerDayCreateModal from "./ExerciseTrackerDayCreateModal";

function ExerciseTracker({show, setShow}: ExerciseTrackerProps) {
  const [data, setData] = useState<Day[]>();
  const [loading, setLoading] = useState(true);

  const { getAccessTokenSilently } = useAuth0();

  const handleClick = async () => {
    try {
      const token = await getAccessTokenSilently();
      alert(token);
    } catch (e) {
      alert(e);
    }
  };

  const fetchData = async () => {
    const data = await get("http://167.179.146.115:5000/api/day", "123");

    setData(data);
    setLoading(false);
  };

  useEffect(() => {
    //fetchData();
    const fetchData = async () => {
      try {
        const token = await getAccessTokenSilently();
        alert(token);
      } catch (error) {
        alert(error);
      }
    };

    fetchData();
  }, [getAccessTokenSilently]);

  return (
    <>
    <Button onClick={handleClick}>Click Me</Button>
      {loading ? <Loading /> : <Row>
        {daysSortedByName(data!).map(day => (
          <Col md={4} className="mt-4">
            <Card>
              <ExerciseTrackerDay day={day} reload={fetchData} />
            </Card>
          </Col>
        ))}

        <ExerciseTrackerDayCreateModal show={show} setShow={setShow} reload={fetchData} />
      </Row>}
    </>
  );
}

export default ExerciseTracker;
