import { Card, Col, Row } from "react-bootstrap";
import {
  Area,
  AreaChart,
  Legend,
  ResponsiveContainer,
  Tooltip,
  XAxis,
  YAxis
} from "recharts";
import { dataFormatter } from "../Helpers/WeightHelpers";
import { WeightTrackerProps } from "../Interfaces/Weight";
import WeightTrackerModal from "./WeightTrackerModal";

function WeightTracker({data, show, setShow, reload}: WeightTrackerProps) {
  const minValue = Math.min(...data!.map((w) => w.goalWeight)) - 2;
  const maxValue = Math.max(...data!.map((w) => w.currentWeight)) + 2;

  return (
    <Row>
      <Col md={8} className="mt-4">
        <Card>
          <Card.Body>
            <Card.Title>Weight Tracker</Card.Title>
          </Card.Body>
        </Card>
      </Col>
      <WeightTrackerModal data={data} show={show} setShow={setShow} reload={reload} />
    </Row>
  );
}

export default WeightTracker;
