import { useEffect, useState } from "react";
import { Card, Col, Container, Row } from "react-bootstrap";
import { Weight } from "./Interfaces/Weight";
import Loading from "./Components/Loading";
import Sidebar from "./Components/Sidebar";
import WeightDifference from "./Components/WeightDifference";
import WeightTracker from "./Components/WeightTracker";
import { Day } from "./Interfaces/Day";
import Foo from "./Components/Day";
import DayModal from "./Components/DayModal";

function App() {
  const [data1, setData1] = useState<Weight[]>();
  const [data2, setData2] = useState<Day[]>();
  const [loading, setLoading] = useState(true);
  const [showWeightModal, setShowWeightModal] = useState(false);
  const [showDayModal, setShowDayModal] = useState(false);

  const fetchData = async () => {
    const response1 = await fetch("http://localhost:5000/api/weight");
    const response2 = await fetch("http://localhost:5000/api/day")

    if (response1.ok && response2.ok) {
      const data1 = await response1.json();
      const data2 = await response2.json();

      setData1(data1);
      setData2(data2);
      setLoading(false);
    }
    else {
      alert("Fuck");
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <Container fluid={true}>
      <Row>
        <Col md="auto" className="mt-4">
          <Sidebar
            setShowWeightModal={setShowWeightModal}
            setShowDayModal={setShowDayModal} />
        </Col>

        <Col>
          <Row>
            <Col md={8} className="mt-4">
              <Card>
                {loading ? <Loading /> : <WeightTracker data={data1} show={showWeightModal} setShow={setShowWeightModal} reload={fetchData} />}
              </Card>
            </Col>

            <Col md={4} className="mt-4">
              <Card>
                {loading ? <Loading /> : <WeightDifference data={data1} />}
              </Card>
            </Col>
          </Row>

          <Row>
            {loading ? <Loading /> : data2!.map(d => (
              <Col md={4} className="mt-4">
                <Card>
                  <Foo day={d} />
                </Card>
              </Col>
            ))}
          </Row>
        </Col>
      </Row>

      <DayModal show={showDayModal} setShow={setShowDayModal} reload={fetchData} />
    </Container>
  );
}

export default App;
