import { useEffect, useState } from "react";
import { Button, Card, OverlayTrigger, Tooltip } from "react-bootstrap";
import { PlusCircle, Trash3Fill } from "react-bootstrap-icons";
import { ExerciseTrackerDayProps } from "../../Interfaces/Day";
import { Exercise } from "../../Interfaces/Exercise";
import Loading from "../Loading";
import ExerciseTrackerDayDeleteModal from "./ExerciseTrackerDayDeleteModal";
import ExerciseTrackerExerciseTable from "./ExerciseTrackerExerciseTable";
import ExerciseTrackerExerciseCreateModal from "./ExerciseTrackerExerciseCreateModal";

function ExerciseTrackerDay({day, reload}: ExerciseTrackerDayProps) {
  const [data, setData] = useState<Exercise[]>();
  const [loading, setLoading] = useState(true);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false);

  const handleDelete = () => setShowDeleteModal(true);
  const handleCreate = () => setShowCreateModal(true);

  const fetchData = async () => {
    const response = await fetch("http://localhost:5000/api/exercise");

    if (response.ok) {
      const data = await response.json();

      setData(data);
      setLoading(false);
    }
    else {
      alert("Fuck");
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <Card.Body>
      <Card.Title>
        {day.name}

        <OverlayTrigger
          key="top"
          placement="top"
          overlay={
            <Tooltip className="tooltip" id="top">Delete Day</Tooltip>
          }
        >
          <Button className="btn-trash float-end ms-3" onClick={handleDelete}>
            <Trash3Fill />
          </Button>
        </OverlayTrigger>

        <OverlayTrigger
          key="top"
          placement="top"
          overlay={
            <Tooltip id="top">Add Exercise</Tooltip>
          }
        >
          <Button className="btn-add float-end ms-3" onClick={handleCreate}>
            <PlusCircle />
          </Button>
        </OverlayTrigger>
      </Card.Title>

      <p>{day.description}</p>

      {loading ? <Loading /> : <ExerciseTrackerExerciseTable data={data} day={day} reload={fetchData} />}

      <ExerciseTrackerDayDeleteModal day={day} show={showDeleteModal} setShow={setShowDeleteModal} reload={reload} />
      <ExerciseTrackerExerciseCreateModal day={day} show={showCreateModal} setShow={setShowCreateModal} reload={fetchData} />
    </Card.Body>
  );
}

export default ExerciseTrackerDay;
