import { useState } from "react";
import { Button, Table } from "react-bootstrap";
import { Pencil } from "react-bootstrap-icons";
import { ExerciseTrackerExerciseTableProps } from "../../Interfaces/Exercise";

function ExerciseTrackerExerciseTable({data, day}: ExerciseTrackerExerciseTableProps) {
  const [showEditModal, setShowEditModal] = useState(false);

  const handleEdit = () => setShowEditModal(true);

  return (
    <Table className="mt-4 mb-0" responsive={true}>
      <thead>
        <tr>
          <th>Name</th>
          <th>Weight</th>
          <th>Reps</th>
          <th>Sets</th>
          <th>Edit</th>
        </tr>
      </thead>
      <tbody>
        {data?.filter(e => e.dayId === day.id).sort((a, b) => a.name.localeCompare(b.name)).map((e =>
          <tr>
            <td>{e.name}</td>
            <td>{e.weight}</td>
            <td>{e.reps}</td>
            <td>{e.sets}</td>
            <td><Button className="btn-edit d-flex"><Pencil /></Button></td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
}

export default ExerciseTrackerExerciseTable;
