import { useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";
import { DateFormatter } from "../Helpers/WeightHelpers";
import { WeightTrackerModalProps } from "../Interfaces/Weight";

function WeightTrackerModal({data, show, setShow}: WeightTrackerModalProps) {
  const [currentWeight, setCurrentWeight] = useState(0);
  const [goalWeight, setGoalWeight] = useState(0);

  const latest = data!.at(-1)!; // Get the last element from the array

  const recordExists = (d: Date) => {
    return DateFormatter(new Date) === DateFormatter(d);
  }

  const handleClose = () => setShow(false);

  const handleSubmit = async (e: React.MouseEvent<HTMLButtonElement, MouseEvent>) => {
    e.preventDefault();



    const response = DateFormatter(currentDate) === DateFormatter(latest.currentDate)
      ? await UpdateWeightRecord(latest.id, latest.currentDate, currentWeight, goalWeight)
      : await CreateWeightRecord(currentDate, currentWeight, goalWeight);
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Body>
        <Modal.Title className="mb-3">Weight Tracker</Modal.Title>

        <Form>
          <Form.Group>
            <Form.Label>Current Weight</Form.Label>
            <Form.Control
              className="mb-3"
              onChange={(e) => setCurrentWeight(Number(e.target.value))}
              placeholder="What is your current weight?"
            />
          </Form.Group>

          <Form.Group>
            <Form.Label>Goal Weight</Form.Label>
            <Form.Control
              className="mb-3"
              defaultValue={latest.goalWeight}
              onChange={(e) => setGoalWeight(Number(e.target.value))}
              placeholder="What is your goal weight?"
            />
          </Form.Group>

          <Form.Group className="d-flex justify-content-end">
            <Button className="foo me-3" onClick={handleClose}>Close</Button>
            <Button className="bar" onClick={handleSubmit} type="submit">Submit</Button>
          </Form.Group>
        </Form>
      </Modal.Body>
    </Modal>
  );
}

export default WeightTrackerModal;
