import { useAuth0 } from "@auth0/auth0-react";
import { useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";
import { dateFormatter, latest } from "../../Helpers/WeightTrackerHelpers";
import { CreateWeightModalProps, Weight } from "../../Interfaces/Weight";
import { patchRecord, postRecord } from "../../Repository";

function CreateWeightModal({weights, show, setShow, reload}: CreateWeightModalProps) {
  const foo = latest(weights)!;

  const [currentDate, setCurrentDate] = useState(new Date());
  const [currentWeight, setCurrentWeight] = useState(foo.currentWeight);
  const [goalWeight, setGoalWeight] = useState(foo.goalWeight);

  const { getAccessTokenSilently } = useAuth0();

  const handleClose = () => setShow(false);

  const handleSubmit = async (e: React.MouseEvent<HTMLButtonElement, MouseEvent>) => {
    e.preventDefault();

    const token = await getAccessTokenSilently();

    if (dateFormatter(foo.currentDate) === dateFormatter(currentDate)) {
      const body: Weight = {
        id: foo.id,
        currentDate: currentDate,
        currentWeight: currentWeight,
        goalWeight: goalWeight
      };

      await patchRecord(`http://167.179.146.115:5000/api/weight/${id}`, token, body);
    }
    else {
      const body: Weight = {
        currentDate: currentDate,
        currentWeight: currentWeight,
        goalWeight: goalWeight
      };

      await postRecord("http://167.179.146.115:5000/api/weight", token, body);
    }

    reload();
    handleClose();
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Body>
        <Modal.Title className="mb-3">Record Weight</Modal.Title>

        <Form>
          <Form.Group>
            <Form.Label>Current Weight</Form.Label>
            <Form.Control
              className="mb-3"
              onChange={(e) => setCurrentWeight(Number(e.target.value))}
              placeholder="What is your current weight?"
            />
          </Form.Group>

          <Form.Group>
            <Form.Label>Goal Weight</Form.Label>
            <Form.Control
              className="mb-3"
              defaultValue={latest.goalWeight}
              onChange={(e) => setGoalWeight(Number(e.target.value))}
              placeholder="What is your goal weight?"
            />
          </Form.Group>

          <Form.Group className="d-flex justify-content-end">
            <Button className="btn-cancel me-3" onClick={handleClose}>Close</Button>
            <Button className="btn-submit" onClick={handleSubmit} type="submit">Submit</Button>
          </Form.Group>
        </Form>
      </Modal.Body>
    </Modal>
  );
}

export default CreateWeightModal;
