import { useState } from "react";
import { Button, Form, Modal, OverlayTrigger, Tooltip } from "react-bootstrap";
import { Trash3Fill } from "react-bootstrap-icons";
import { ExerciseTrackerExerciseEditModalProps } from "../../Interfaces/Exercise";

function ExerciseTrackerExerciseEditModal({day, exercise, show, setShow, reload}: ExerciseTrackerExerciseEditModalProps) {
  const [name, setName] = useState("");
  const [weight, setWeight] = useState(0);
  const [reps, setReps] = useState(0);
  const [sets, setSets] = useState(0);

  const handleClose = () => setShow(false);

  const handleSubmit = async (e: React.MouseEvent<HTMLButtonElement, MouseEvent>) => {
    e.preventDefault();

    alert(`${name}${weight}${reps}${sets}`);
  };

  const handleDelete = async () => {}

  return (
    <Modal show={show} onHide={handleClose} centered={true}>
      <Modal.Body>
        <Modal.Title className="mb-3">
          Update Exercise

          <OverlayTrigger
            key="top"
            placement="top"
            overlay={
              <Tooltip className="tooltip" id="top">Delete Exercise</Tooltip>
            }
          >
            <Button className="btn-trash float-end" onClick={handleDelete}>
              <Trash3Fill />
            </Button>
          </OverlayTrigger>
        </Modal.Title>

        <Form>
          <Form.Group>
            <Form.Label>Name</Form.Label>
            <Form.Control
              className="mb-3"
              defaultValue={exercise.name}
              onChange={(e) => setName(e.target.value)}
              placeholder="What is the name of the exercise?"
            />
          </Form.Group>

          <Form.Group>
            <Form.Label>Weight</Form.Label>
            <Form.Control
              className="mb-3"
              defaultValue={exercise.weight}
              onChange={(e) => setWeight(Number(e.target.value))}
              placeholder="What is the weight used for the exercise?"
            />
          </Form.Group>

          <Form.Group>
            <Form.Label>Reps</Form.Label>
            <Form.Control
              className="mb-3"
              defaultValue={exercise.reps}
              onChange={(e) => setReps(Number(e.target.value))}
              placeholder="What is the number of reps for the exercise?"
            />
          </Form.Group>

          <Form.Group>
            <Form.Label>Sets</Form.Label>
            <Form.Control
              className="mb-3"
              defaultValue={exercise.sets}
              onChange={(e) => setSets(Number(e.target.value))}
              placeholder="What is the number of sets for the exercise?"
            />
          </Form.Group>

          <Form.Group className="d-flex justify-content-end">
            <Button className="btn-cancel me-3" onClick={handleClose}>Close</Button>
            <Button className="btn-submit" onClick={handleSubmit} type="submit">Update</Button>
          </Form.Group>
        </Form>
      </Modal.Body>
    </Modal>
  );
}

export default ExerciseTrackerExerciseEditModal;
