import { useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";
import { Day, DayModalProps } from "../Interfaces/Day";

function DayModal({show, setShow, reload}: DayModalProps) {
  const [name, setName] = useState("Monday");

  const handleClose = () => setShow(false);

  const handleSubmit = async (e: React.MouseEvent<HTMLButtonElement, MouseEvent>) => {
    e.preventDefault();

    const day: Day = {
      name: name
    };

    const response = await fetch("http://localhost:5000/api/day", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(day),
    });

    if (response.ok) {
      handleClose();
      reload();
    }
    else {
      alert("Fuck");
    }
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Body>
        <Modal.Title className="mb-3">Add Exercise Day</Modal.Title>

        <Form>
          <Form.Group>
            <Form.Label>Day</Form.Label>
            <Form.Select className="mb-3" onChange={(e) => setName(e.target.value)}>
              <option className="current-line" value="Monday">Monday</option>
              <option value="Tuesday">Tuesday</option>
              <option value="Wednesday">Wednesday</option>
              <option value="Thursday">Thursday</option>
              <option value="Friday">Friday</option>
              <option value="Saturday">Saturday</option>
              <option value="Sunday">Sunday</option>
            </Form.Select>
          </Form.Group>

          <Form.Group className="d-flex justify-content-end">
            <Button className="foo me-3" onClick={handleClose}>Close</Button>
            <Button className="bar" onClick={handleSubmit} type="submit">Submit</Button>
          </Form.Group>
        </Form>
      </Modal.Body>
    </Modal>
  );
}

export default DayModal;
