import Loading from "./Loading";
import WeightTrackerAreaChart from "./WeightTrackerAreaChart";

function WeightTracker({foo}: WeightTrackerProps) {
  const [data, setData] = useState<Weight[]>();
  const [loading, setLoading] = useState(true);

  const fetchData = async () => {
    const response = await fetch("http://localhost:5000/api/weight");

    if (response.ok) {
      const data = await response.json();

      setData(data);
      setLoading(false);
      foo(data);
    }
    else {
      // Fuck
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <Card>
      {loading ? <Loading /> : <WeightTrackerAreaChart data={data} />}

      <WeightTrackerModal data={data} show={show} setShow={setShow} />
    </Card>
  );
}

export default WeightTracker;
